+          <li class="list-group-item"><b>{{ _('Temporal Coverage') }}{{ _(':') }}</b>
+            <small>
+              {% if start_date and end_date and end_date != '9999-09-09' %}
+                {{ start_date }} {{ _('to') }} {{ end_date }}
+              {% elif start_date %}
+                {{ start_date }} {{ _('to undefined') }}
+              {% elif end_date %}
+                {{ _('undefined to') }} {{ end_date }}
